import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);

  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });
  const FetchContent = (props) => {
    console.log(props);
    const apiUrl = "http://api-v2.filmnet.ir";
    useEffect(() => {
      if(appState.nextURL === null){
        const url ="/widgets/home-page?count=5"
        setAppState({ loading: true });
        fetch(apiUrl + url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({
              loading: false,
              repos: repos.data,
              nextURL: repos.meta.next_url,
            });
            console.log(repos);
          });
      }else{
        setAppState({ loading: true });
        fetch(apiUrl + props)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({
              loading: false,
              repos: repos.data,
              nextURL: repos.meta.next_url,
            });
            console.log(repos);
          });
      }  

    }, [setAppState]);
  };
  return (
    <InfiniteScroll
      dataLength={Object.values(appState)}
      next={FetchContent(appState.nextURL)}
      hasMore={true}
    >
    {Object.values(appState).map(()=>{

        c
      {/* <ListLoading
        isLoading={appState.loading}
        repos={appState.repos}
      /> */}
    })}

    </InfiniteScroll>
  );
};
export default FetchContents;
