import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";
import Slideshow from "./../slide/slideShow";
import Card from "./../card/card";
import "./FetchContent.css";
import NavBar from "../navBar/navBar";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import Footer from "../footer/footer";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(0),
    textAlign: "start",
    color: theme.palette.text.primary,
    backgroundColor: theme.palette.background.default,
  },
}));
const FetchItems = () => {
  const classes = useStyles();
  const apiurl = "http://api-v2.filmnet.ir/widgets/home-page?offset=";
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState([]);
  const [count, setCount] = useState(0);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {
    // getData(count);
  }, [count]);


  const arr = state.map((items,index) => <Slideshow {...items.data} key={index+1} />);
  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={classes.paper}>
            <div>{arr}</div>
            <NavBar />
            <Card />
            <InfiniteScroll
              className="scroll"
              dataLength={state.length}
              next={() => setCount((prevCount) => prevCount + 5)}
              hasMore={hasMore}
              val={console.log(count)}
              loader={<p className="loader">دریافت موارد بیشتر ...</p>}
            >
              {state.map((item, index) => (
                <ListLoading isLoading={false} repos={item} key={index} />
              ))}
            </InfiniteScroll>
            <Footer />
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};
export default FetchItems;
