import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import Img1 from "./image/1.jpg";
// import Img2 from "./image/2.jpg";
// import Img3 from "./image/3.jpg";

const slideImages = [
  "https://static.filmnet.ir/images/94/51/94511e5d24fd4c799f94f16a83d1842f.jpg",
  "https://static.filmnet.ir/images/74/24/7424f63029db41bd9b35df817983c55e.jpg",
  "https://static.filmnet.ir/images/81/62/816279b8cbb54ba4b2911b7b59a2cd91.jpg",
];
const photos = [
  {
    id: 1,
    title: "photo A",
    background:  "1.jpg",
    topic: "topic A",
  },
  {
    id: 2,
    title: "photo B",
    background: "2.jpg",
    topic: "topic B",
  },
  {
    id: 3,
    title: "photo C",
    background:  "3.jpg",
    topic: "topic C",
  },
];
class Slideshow extends React.Component {
  render() {
    var settings = {
      dots: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    return (
        <div style={{ padding: 24 }}>

          <Slider {...settings}>
            {photos.map(item => (
              <div key={item.id} className="item-slider" style={{
                 'background': "url(" + process.env.PUBLIC_URL+`/image/${item.background}` + ")", backgroundRepeat: "no-repeat"
                // 'background': 'url(' + require(`./image/${item.background}`)+ ')'
              }}>
              </div>
            ))}
          </Slider>
        </div>

    );
  }
}

export default Slideshow;
