import React from "react";
import "./navBar.css";
import Link from "@material-ui/core/Link";
import List from "@material-ui/core/List";
import { Switch, Route } from "react-router-dom";
import TextField from "@material-ui/core/TextField";
import InputAdornment from "@material-ui/core/InputAdornment";
import Package from "../package/Package";
const NavBarBtn = () => {
  const [active, setActive] = React.useState(null);

  return (
    <>
      <List className="navBarList">
        <Switch>
          <Route className="navBarBtn" path="/login">
            <button className="Btn">تبت نام/ ورود</button>
          </Route>
          <Route className="navBarBtn" path="/packages" exact component={Package}>
            <button className="Btn2">خرید اشترک</button>
          </Route>
        </Switch>
      </List>
      <div
        tabIndex="1"
        className={`search ${active}`}
        onClick={() => setActive("activeLine")}
        onBlur={() => setActive(null)}
      >
        <TextField
          placeholder="جستجو کردن ..."
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <img
                  src="https://filmnet.ir/static/images/ic-search-off.png"
                  alt="جستجو"
                />
              </InputAdornment>
            ),
          }}
        />
      </div>
    </>
  );
};
export default NavBarBtn;
