import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "/widgets/home-page?count=5",
  });
  f
  function Fetch(x) {
    useEffect(() => {
      const x = appState.nextURL;
      FetchContent(x);
    }, [setAppState]);
  }
  function FetchContent(nextUrl) {
    console.log(nextUrl);
    const apiUrl = "http://api-v2.filmnet.ir";

    // if (nextUrl === null) {
    // const url = "/widgets/home-page?count=5";
    setAppState({ loading: true });
    fetch(apiUrl + nextUrl)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({
          loading: false,
          repos: repos,
          nextURL: repos.meta.next_url,
        });
      });
  }
  return (
    <InfiniteScroll
      dataLength={new Array(20)}
      //next={FetchContent(appState.nextURL)}
      hasMore={true}
      val={console.log(appState.nextURL)}
    >
      <ListLoading isLoading={appState.loading} repos={appState.repos} />
    </InfiniteScroll>
  );
};
export default FetchContents;
