import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slidesShow.css";
import { Button } from "@material-ui/core";

const settings = {
  dots: true,
  fade: true,
  infinite: true,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
};
const Slideshow = (props) => {
  const vals = Object.values(props);
  const val = vals.map((items) => {
    if (items.type === "main_slider") {
      const value = Object.values(items.configuration);
      return value;
    }
  });
  return (
    <div className="slides">
      {val.map((item) => (
        <Slider {...settings}>
        <div
                    key={item.image_id}
                    val={item.image_path}
                    className="item-slider"
                    style={{
                      backgroundRepeat: "no-repeat",
                      background: "url(" + item.image_path + ")",
                    }}
                  ></div>
                   <div className="slide-btns" >
                    <Button href="/" className="slide-btn">
                    خرید بلیط
                    </Button>
                  </div> 
        </Slider>
      ))}
      ;
    </div>
  );
};

export default Slideshow;
