import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);

  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });

  useEffect(() => {
    const apiUrl = `http://api-v2.filmnet.ir`;
    if (appState.nextURL) {
      setAppState({ loading: true });
      fetch(apiUrl + appState.nextURL)
        .then((res) => res.json())
        .then((repos) => {
          setAppState({
            loading: false,
            repos: repos,
            nextURL: repos.meta.next_url,
          });
        });
    } else {
      setAppState({ loading: true });
      const url = "/widgets/home-page?count=5";
      fetch(apiUrl + url)
        .then((res) => res.json())
        .then((repos) => {
          setAppState({
            loading: false,
            repos: repos,
            nextURL: repos.meta.next_url,
          });
        });
    }
  }, [setAppState]);

  const FetchContent = (props) => {
    console.log(props);
    // setTimeout(() => {
    //   setAppState({
    //     loading: props.,
    //     repos: null,
    //     nextURL: props.nextURL,
    //   });
    // }, 1800);
  };

  return (
    <InfiniteScroll
      dataLength={appState.repos}
      next={FetchContent(appState)}
      hasMore={true}
    >
      {/* {appState.repos.map((i,index) => ( */}
      <ListLoading
        key={appState.nextURL}
        isLoading={appState.loading}
        repos={appState.repos}
      />
      {/* ))} */}
      {}
    </InfiniteScroll>
  );
};
export default FetchContent;
