import React, { useState } from "react";
import "./filter.css";

const FiltersArrow = () => {

  const scrollTop = () => {};

  return (
    <div className="filter" onClick={scrollTop}>
      <span className="filter-items">
        <img
          src="https://filmnet.ir/static/images/ic_filter.svg"
          alt="filter"
        />
        <span>فیلترها</span>
      </span>
    </div>
  );
};

export default FiltersArrow;
