import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

export default function Test() {
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: [],
    nextURL: null,
    hasMoreItems: true,
  });
  const [Url, setUrl] = useState([]);
  useEffect(() => {
    getData("/widgets/home-page?count=5");
  },[state.nextURL]);
  function loadMore(url) {
    //console.log(url);
    //const array2 = url;
    //url = url.filter(val => !array2.includes(val));
    const allEqual = arr => arr.every( v => v === arr[0] )
    const ali = allEqual(url);
    url =  url.shift();
    console.log(url);
    // for (let index = 0; index < url.length; index++) {
    //   const element = url[index];
    //   console.log(element);
    //   if (element === url[index]){
    //     console.log(url[index]);
    //     return url[index]
    //   }
    // }
    //setUrl(url);
    //getData(state.nextURL);
  }
  function getData(url) {
    const apiurl = "http://api-v2.filmnet.ir";
    var URL = apiurl + url;
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        if (res.meta.next_url) {
          setState({
            loading: false,
            repos: res,
            nextURL: res.meta.next_url,
          });
        } else {
          setState({
            hasMoreItems: false,
          });
        }
        setUrl((prev)=>[...prev,res.meta.next_url])
      });
  }
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={loadMore(Url)}
      hasMore={state.hasMoreItems}
    >
      <ListLoading isLoading={state.loading} repos={state.repos} />
    </InfiniteScroll>
  );
}
