import React, { useState, useEffect, useRef } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slidesShow.css";
import { Button } from "@material-ui/core";

const Gallery = (props) => {
  const [state, setState] = useState({
    nav1: null,
  });
  const vals = Object.values(props);
  const val = vals[0].map((items) => {
    if (items.type === "main_slider") {
      const values = Object.values(items.configuration);
      const arr = values.map((value) => {
        return value;
      });
      return arr;
    }
  });
  const array = val.filter((item) => item !== undefined);
  const arr = array[0];
  const list = arr[0];
  //return <div>1</div>;
  const element = list.map((item) => {
    return Object.values(item);
  });
  console.log(element);
  const slider = useRef();

  useEffect(() => {
    setState({
      nav: slider.current,
    });
  }, []);

  const { nav1 } = state;

  const settingsSliderNav = {
    slidesToShow: 3,
    slidesToScroll: 1,
    dots: false,
    arrows: false,
    focusOnSelect: false,
    infinite: true,
    speed: 500,
  };

  return (
    <React.Fragment>
      <Slider
        asNavFor={nav1}
        ref={(slider) => (slider.current = slider)}
        {...settingsSliderNav}
      >
        {element.map((item, index) => (
          <div className="slides" val={console.log(item)} key={index}>
            <div val={console.log(item)} key={item[2]} className="item-slider">
              <img src={item[3]} alt={item[0]} key={index + item[2]} />
            </div>
            <div className="slide-btns" key={item[2] + index}>
              <Button href="/" className="slide-btn">
                خرید بلیط
              </Button>
            </div>
          </div>
        ))}
        ;
      </Slider>
    </React.Fragment>
  );
};

export default Gallery;
