import React, { Fragment } from "react";
import StarIcon from "@material-ui/icons/Star";
import IMDB from "../../image/imdb.png";
import "./ContentItem.css";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));
const ContetntItem = (props) => {
  const vals = Object.values(props);
  const elements = vals;
  const element = Object.values(elements[1]);
  const items = element[0];
  const item = Object.values(items);
  return (
    <div className="items">
      {item.map((data) => {
        return (
          <div className={classes.root}>
          <Grid container spacing={1}>
        <Grid item xs={1.5}>
          <div className="item" key={data.content_id}>
            <a href={"https://filmnet.ir/contents/"+(data.video_content.short_id)+"/"+(data.video_content.supper_title)}>
              <img
                src={data.video_content.cover_image.path}
                className="item-img"
                alt="عکس فیلم"
              />
              <div>
                <p className="item-header">{`${data.video_content.title}`}</p>
                <div className="item-text">
                  <span className="item-icon">
                    <StarIcon />{" "}
                  </span>
                  <span className="item-rate">
                    {Math.round(data.video_content.rate)/10}
                  </span>
                  <span className="item-imdb-text">{}</span>
                  <img src={IMDB} alt="IMDB" className="item-imdb-img" />
                </div>
              </div>
            </a>
          </div>
          </Grid>
          </Grid>
          </div>
        );
      })}
    </div>
  );
};
export default ContetntItem;
