import React, { useState, useLayoutEffect } from "react";


const SlideWidth = () => {

  function responsive(props) {
    const [width, height] = useWindowSize();
    if ({ width } >= 1024 && { height } >= 768) {
      return `?w=1500`;
    }
    if (720 <= { width } && { width } <= 1024) {
      return `?w=720`;
    }
    if ({ width } <= 720) {
      return `?w=480`;
    }
  }
  const x = responsive;
  console.log(x);
  return x;
};
export default SlideWidth;
