import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);
  const apiUrl = `http://api-v2.filmnet.ir`;
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    hasMore: false,
    url: null,
  });
  const [moreState, setMoreState] = useState({
    loading: false,
    repos: null,
    url:null
  });
  const url="/widgets/home-page?count=5";
  useEffect(() => {
    setAppState({ loading: true });
    fetch(apiUrl +url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos,url: repos.meta.next_ur});
      });
  }, [setAppState]);
const fetch = ()=>{
  useEffect(() => {
    console.log(url);
    fetch(apiUrl + moreState.url)
    .then((res) => res.json())
    .then((data) => {
      setMoreState({
        loading: false,
        data: data,
      });
      console.log(data);
    });
  },[setMoreState]);
}
  return (
    <div>
      <ListLoading isLoading={appState.loading} repos={appState.repos} />
      {/* {fetchMore} */}
      <ListLoading isLoading={moreState.loading} repos={moreState.data} />
    </div>
  );
};
export default FetchContent;
