import React, { Fragment } from "react";
import StarIcon from "@material-ui/icons/Star";
import IMDB from "../../image/imdb.png";
import "./ContentItem.css";

const ContetntItem = (props) => {
  const vals = Object.values(props);
  // const item =Object.values(val.items);
  for (let index = 0; index < vals.length; index++) {
    const elements = vals[1];
    console.log(elements)
    const element = Object.values(elements);

    return (
      <Fragment>
        {element.map((val) => {
          {/* console.log(val); */}
          val.map((data) => {
            console.log((data));

            return (
              <div className="item" key={val.content_id}>
                <a href={val.video_content}>
                  <img
                    src={val.video_content.cover_image.path}
                    className="item-img"
                    alt="عکس فیلم"
                  />
                  <div>
                    <p className="item-header">{`${val.video_content.title}`}</p>
                    <div className="item-text">
                      <span className="item-icon">
                        <StarIcon />{" "}
                      </span>
                      <span className="item-rate">
                        {val.video_content.rate % 10}
                      </span>
                      <span className="item-imdb-text">{}</span>
                      <img src={IMDB} alt="IMDB" className="item-imdb-img" />
                    </div>
                  </div>
                </a>
              </div>
            );
          });
        })}
        ;
      </Fragment>
    );
  }
};
export default ContetntItem;
