import Typography from "@material-ui/core/Typography";
import React, { Fragment } from "react";
import "./ContentHeader.css";
import ContetntItem from "./newContentItem";

const ContentHeader = (props) => {
  const val = Object.values(props);
  return (
    <Fragment>
      {val.map((repo) => {
        if (repo.type === "regular_carousel") {
          return (
            <div className="contenet-header" key={repo.id}>
              <Typography
                variant="h6"
                component="h8"
                className=""
                key={repo.id}
              >
                {`${repo.display_title}`}
              </Typography>
              <a
                key={repo.configuration.navigation_configuration.url}
                href={repo.configuration.navigation_configuration.url}
              >
                <button className="contenet-header-btn">مشاهده همه</button>
              </a>

              <ContetntItem {...repo} />
            </div>
          );
        }
      })}
    </Fragment>
  );
};

export default ContentHeader;
