import React, { Fragment } from "react";
import "./ContentHeader.css";
import ContetntItem from "./ContentItem";

const ContentHeader = (props) => {
  const val = Object.values(props)
  return (
    <Fragment key>
      {val.map(repo => {
        if (repo.type === "regular_carousel") {
          return (
            <div className="main" key={repo.id}>
              <div className="contenet-header" key={repo.id} >
                <h6 className="contenet-header-text" key={repo.display_title}>{`${repo.display_title}`}</h6>
                <a key={repo.configuration.navigation_configuration.url} href={repo.configuration.navigation_configuration.url}>
                  <button className="contenet-header-btn">مشاهده همه</button>
                </a>
              </div>
              <ContetntItem {...repo} />
            </div>
          );
        }
      })}
    </Fragment>
  );
};

export default ContentHeader;
