import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);
  const apiUrl = `http://api-v2.filmnet.ir`;
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    hasMore: false,
    url: "/widgets/home-page?count=5",
    data: null,
  });

  useEffect(() => {
    setAppState({ loading: true });

    fetch(apiUrl + appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos });
        console.log(repos);
        fetch(apiUrl + repos.meta.next_url)
          .then((res) => res.json())
          .then((data) => {
            setAppState({
              data: data,
            });
            console.log(data);
          });
      });
  }, [setAppState]);

  const fetchMore = (page,pageCurrent,rep) => {
    if(page !== pageCurrent){ 
      return<ListLoading isLoading={appState.loading} repos={appState.data} />;
    }
  };
  return (
    <div>
      <ListLoading isLoading={appState.loading} repos={appState.repos} />

    </div>
  );
};
export default FetchContent;
