import React, { useState, useEffect } from "react";
import InfiniteScroll from "react-infinite-scroller";
import Posts from "./ContentLoading";

function PostsContainer() {
  const [data, setData] = useState();
  const [shownData, setShownData] = useState();
  const [page] = useState([]);
  const URL = "http://api-v2.filmnet.ir";
  const pageURL = "/widgets/home-page?count=5";

  useEffect(() => {
    fetchPosts(pageURL);
  }, [page]);
  async function fetchPosts(count) {
    let res = await fetch(URL+count);
    let json = await res.json();
    let itemsToShow = selectNItems(json.data,json.data.length);
    setShownData(itemsToShow);
    setData(json);
  }
  const selectNItems = (obj, n) => {
    console.log(obj);
    return obj.slice(0, n);
  };

  const loadMorePosts = (url) => {
    let items = fetchPosts();
    setShownData(items);
  };

  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={loadMorePosts()}
      hasMore={data && shownData && data.length > shownData.length}
      loader={<div>Loading ...</div>}
      useWindow={true}
    >
      <Posts posts={shownData} />
    </InfiniteScroll>
  );
}

export default PostsContainer;
