import React from "react";
import StarIcon from "@material-ui/icons/Star";
import IMDB from "../../image/imdb.png";
// import url from "../../image/movie1.jpg";
import "./ContentItem.css";
const ContetntItem = ({urlMovie,urlImage,name,rate,imdb}) => {
  return (
    <div className="item">
      <a href={urlMovie}>
        <img src={urlImage} className="item-img" alt="عکس فیلم" />
        <div>
          <p className="item-header">{`${name}`}</p>
          <div className="item-text">
            <span className="item-icon"><StarIcon /> </span>
            <span className="item-rate">{rate}</span>
            <span className="item-imdb-text">{imdb}</span>
            <img src={IMDB} alt="IMDB" className="item-imdb-img" />
          </div>
        </div>
      </a>
    </div>
  );
};
export default ContetntItem;
