  var i = 0;
  async function getData(url) {
    i++;
    console.log(i);

    var URL = apiurl + url;
    await fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        // console.log(res);
        if (res.meta.next_url) {
          setState({
            loading: false,

            nextURL: res.meta.next_url,
          });
          setState((prev) => [...prev, res]);
        } else {
          setState({
            hasMoreItems: false,
          });
        }
        // setUrl((prev)=>[...prev,res.meta.next_url])
      });
  }
  return (
      <InfiniteScroll
        pageStart={0}
        // initialLoad={false}
        // threshold={700}
