import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";
export default function App() {
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: [],
    nextURL: null,
    hasMoreItems: true,
  });
  const [url, setUrl] = useState();
  useEffect(() => {
    getData("/widgets/home-page?count=5");
  });
  function loadMore(url) {
    console.log(url);
    //setUrl(url);
    //getData(state.nextURL);
  }
  function getData(url) {
    const apiurl = "http://api-v2.filmnet.ir";
    var URL = apiurl + url;
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        if (res.meta.next_url) {
          setState({
            loading: false,
            repos: res,
            nextURL: res.meta.next_url,
          });
        } else {
          setState({
            hasMoreItems: false,
          });
        }
      });
  }
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={oadMore(state.nextURL)}
      hasMore={state.hasMoreItems}
    >
      <ListLoading isLoading={state.loading} repos={state.repos} />
    </InfiniteScroll>
  );
}
