import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);

  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "",
  });
  const FetchContent = (props) => {
    console.log(props);
    const apiUrl = `http://api-v2.filmnet.ir`;
    useEffect(() => {
      if(appState===n)  
        setAppState({ loading: true });
        fetch(apiUrl + appState.nextURL)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({
              loading: false,
              repos: repos.data,
              nextURL: repos.meta.next_url,
            });
            console.log(repos);
          });
    }, [setAppState]);
  };
  return (
    <InfiniteScroll
      dataLength={appState.repos}
      next={FetchContent(appState.nextURL)}
      hasMore={true}
    >
      <ListLoading
        isLoading={appState.loading}
        repos={appState.repos}
      />
    </InfiniteScroll>
  );
};
export default FetchContents;
