import React, { useEffect, useState } from "react";
import ContentHeader from "./ContentHeader";
import ContentImg from "./ContentImg";
import "./ContentItem.css";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import InfiniteScroll from "react-infinite-scroller";
import FetchContent from "./FetchContent";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(0),
    textAlign: "start",
    color: theme.palette.text.primary,
    backgroundColor: theme.palette.background.default,
  },
}));
const ContetntItems = (props) => {

  const classes = useStyles();
  const { repos } = props;
  if (!repos || repos.length === 0) return <p>server don't responce , sorry</p>;
  const val = Object.values(repos);
  const baseURL = val[1].next_url;

  return (
    <div className={classes.root}>
      <InfiniteScroll
        pageStart={0}
        loadMore={<FetchContent url={baseURL}/>}
        hasMore={this.state.hasMoreItems}
      >
        {val.map((repo) => {
          return (
            <Grid container spacing={3} className="content-body">
              <Grid item xs={12} key={repo}>
                <Paper className={classes.paper} style={{ boxShadow: "none" }}>
                  <ContentHeader {...repo} />
                  <ContentImg {...repo} />
                </Paper>
              </Grid>
            </Grid>
          );
        })}
      </InfiniteScroll>
    </div>
  );
};

export default ContetntItems;
