import React, { useCallback, useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "/widgets/home-page?count=5",
    hasMore: true,
  });
  const [page] = useState([]);

  useEffect(() => {
    Fetch();
  }, [page]);

  async function Fetch() {
    const apiUrl = "http://api-v2.filmnet.ir";
    const URL = apiUrl + appState.nextURL;
    setAppState({ loading: true });
    const realPage = page + 1;
    const resp = await fetch(`${URL}&page=${realPage}`);
    const repos = await resp.json();
    setAppState({
      loading: false,
      repos: repos,
      nextURL: repos.meta.next_url,
    });
    if (!repos.meta.next_url) {
      setAppState({ hasMore: false });
    }
    // setPage([appState]);
  }

  // function FetchMore(url) {
  //   console.log(url);
  //   //Fetch(url);
  // }
  return (
    <div id="scrollableDiv" style={{ height: "auto", overflow: "auto" }}>
      <InfiniteScroll
        dataLength={window.length}
        hasMore={appState.hasMore}
        inverse={true}
        scrollableTarget="scrollableDiv"
        key={console.log(appState.nextURL)}
        next={()=>setAppState({nextURL:appState.repos})}
      >
        {page.map((item, index) => (
          <ListLoading
            key={index}
            isLoading={item.loading}
            repos={item.repos}
          />
        ))}
      </InfiniteScroll>
    </div>
  );
};
export default FetchContents;
