import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

export default function Test() {
  const apiurl = "http://api-v2.filmnet.ir";
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: [],
    nextURL: "/widgets/home-page?count=5",
    hasMoreItems: true,
  });
  //const [Url, setUrl] = useState([]);

  console.log("jafarrr");
  useEffect(() => {

    getData(state.nextURL); 
  }, []);

  // function loadMore(url) {
  //   //console.log(url);
  //   //const array2 = url;
  //   //url = url.filter(val => !array2.includes(val));
  //   console.log(url, "-------");
  //   // const allEqual = arr => arr.every( v => v === arr[0] )
  //   // if(allEqual(url)){
  //     // url =  url.shift();
  //     //getData("/widgets/home-page?offset=5&count=5");
  //   // }
  //   // for (let index = 0; index < url.length; index++) {
  //   //   const element = url[index];
  //   //   console.log(element);
  //   //   if (element === url[index]){
  //   //     console.log(url[index]);
  //   //     return url[index]
  //   //   }
  //   // }
  //   //setUrl(url);
  //   //
  // }

  var i = 0;
  async function getData(url) {
    i++;
    console.log(i);

    var URL = apiurl + url;
    await fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        // console.log(res);
        if (res.meta.next_url) {
          setState({
            loading: false,
            repos: res,
            nextURL: res.meta.next_url,
          });
        } else {
          setState({
            hasMoreItems: false,
          });
        }
        // setUrl((prev)=>[...prev,res.meta.next_url])
      });
  }
  const loader = <div className="loader">دریافت موارد بیشتر ...</div>;
  return (
      <InfiniteScroll
        pageStart={0}
        // initialLoad={false}
        // threshold={700}
        loadMore={getData}
        hasMore={state.hasMoreItems}
        val={console.log(state)}
        useWindow={true}
        loader={loader}
      >
        <ListLoading isLoading={state.isLoading} repos={state.repos} />
      </InfiniteScroll>
  );
}
