import React from "react";
import "./ContentHeader.css";
import PropTypes from 'prop-types';

const ContentHeader = ({ janr, fullview,view }) => {
  return (
    <div className="contenet-header">
      <h6 className="contenet-header-text">{`${janr}`}</h6>
      <a href={fullview}>
        <button className="contenet-header-btn" onClick={view}>مشاهده همه</button>
      </a>
    </div>
  );
};
ContentHeader.propTypes = {
  jha :PropTypes.string,
  fullview :PropTypes.string,
  view: PropTypes.func
};
export default ContentHeader;
