import React, { useState, useEffect, useCallback } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import useInfiniteScroll from "@closeio/use-infinite-scroll";

const URL = "http://api-v2.filmnet.ir";
const pageURL = "/widgets/home-page?count=5";
export default function Example() {
  const ListLoading = ContentLoading(ContentItems);
  const [items, setItems] = useState([]);
  const [hasMore, setHasMore] = useState(false);
  const [page, loaderRef, scrollerRef] = useInfiniteScroll({ hasMore });
  const [next, setNext] = useState();
  useEffect(() => {
    Fetch(pageURL);
  }, []);

  async function Fetch(url) {
    const realPage = page + 1;
    const resp = await fetch(`${URL + url}&page=${realPage}`);
    const data = await resp.json();
    setItems((prev) => [...prev, data]);
    setHasMore(true);
    // console.log(data.meta.next_url);
    setNext(data.meta.next_url);
  }

 function FetchMore(props, prevPrpos) {
  //   props.map((item, index) => {
  //     prevPrpos.map((val) => {
        console.log(prevPrpos);
        // if (item.meta.next_url === val) {
        // console.log(val);
        Fetch(prevPrpos);
        // }
      // });
    // });
  }
  return (
    <div ref={scrollerRef} className="1">
      {items.map((item, index) => (
        <ListLoading isLoading={false} repos={item} key={index} />
      ))}

      {FetchMore(items, next) && <div ref={loaderRef} style={{margin:"auto"}}>دریافت موارد بیشتر </div>}

      {/* {console.log(items)} */}
    </div>
  );
}
