import Typography from "@material-ui/core/Typography";
import React, { Fragment } from "react";
import "./ContentHeader.css";
import ContetntItem from "./newContentItem";
import Grid from "@material-ui/core/Grid";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const ContentHeader = (props) => {
  const val = Object.values(props);
  return (
    <div className={classes.root}>
      {val.map((repo) => {
        if (repo.type === "regular_carousel") {
          return (
      <Grid container spacing={3} className="contenet-header" key={repo.id}>
      <Grid item xs={12}>
              <Typography
                variant="h6"
                component="h8"
                key={repo.id}
              >
                {`${repo.display_title}`}
              </Typography>
              <a
                key={repo.configuration.navigation_configuration.url}
                href={repo.configuration.navigation_configuration.url}
              >
                <button className="contenet-header-btn">مشاهده همه</button>
              </a>
</Grid>
<Grid item xs={12}>

              <ContetntItem {...repo} />
</Grid>
           </Grid> 
          );
        }
      })}
    </div>
  );
};

export default ContentHeader;
