import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "/widgets/home-page?count=5",
    hasMore: true,
  });
  const [page, setPage] = useState({items:Array});
  Fetch(appState.nextURL);
  setPage(i)
  function Fetch(nextUrl) {
    useEffect(() => {
      const apiUrl = "http://api-v2.filmnet.ir";
      const URL = apiUrl + nextUrl;
      setAppState({ loading: true });
      fetch(URL)
        .then((res) => res.json())
        .then((repos) => {
          setAppState({
            loading: false,
            repos: repos,
            nextURL:repos.meta.next_url
          });
          setPage([appState]);

          if (!repos.meta.next_url) {
            setAppState({ hasMore: false });
          }
        });
    }, [setAppState]);
  }

  // function FetchMore(url) {
  //   console.log(url);
  //   Fetch(url);
  // }
  return (
    <InfiniteScroll
      dataLength={window.length}
      hasMore={appState.hasMore}
      inverse={true}
      style={{ display: "flex", flexDirection: "column-reverse" }}
      // key={Fetch(appState.nextURL)}
      next={Fetch(appState.nextURL)}
    >
      {page.items.map((item, index) => (
        <ListLoading key={index} isLoading={appState.loading} repos={appState.repos} val ={console.log(page)}/>
      ))}
    </InfiniteScroll>
  );
};
export default FetchContents;
