import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "/widgets/home-page?count=5",
    hasMore: true,
  });
  const [page, setPage] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });
  function Fetch(x) {
    useEffect(() => {
      FetchContent(x);
      getUrl(appState.nextURL,page.nextURL)
    }, [setPage, setAppState]);
  }

  function FetchContent(nextUrl) {
    const apiUrl = "http://api-v2.filmnet.ir";
    const URL = apiUrl + nextUrl;
    setAppState({ loading: true });
    fetch(URL)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({
          loading: false,
          repos: repos,
          nextURL: repos.meta.next_url,
        });
        setPage({
          loading: false,
          repos: repos,
          nextURL: repos.meta.next_url,
        });
        if (!repos.meta.next_url) {
          setAppState({ hasMore: false });
        }
      });
  }
  function getUrl (url,url2){

      console.log(url);
   console.log(url2);
  }  
  return (
    <InfiniteScroll
      dataLength={20}
      hasMore={true}
      inverse={true}
      style={{ display: "flex", flexDirection: "column-reverse" }}
      next={Fetch(appState.nextURL)}
      val={console.log(appState.nextURL=== page.nextURL)}
    >
      <ListLoading isLoading={appState.loading} repos={appState.repos} />
    </InfiniteScroll>
  );
};
export default FetchContents;
