import React from 'react';
import { Fade } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import './../../image/7424f63029db41bd9b35df817983c55e.jpg';
import './../../image/94511e5d24fd4c799f94f16a83d1842f.jpg';
import './../../image/816279b8cbb54ba4b2911b7b59a2cd91.jpg';

const fadeImages = [
  '/../../image/7424f63029db41bd9b35df817983c55e.jpg',
  '/src/image/94511e5d24fd4c799f94f16a83d1842f.jpg',
  '/src/image/816279b8cbb54ba4b2911b7b59a2cd91.jpg'
];

const Slideshow = () => {
    return (
      <div className="slide-container">
        <Fade>
          <div className="each-fade">
            <div className="image-container">
              <img src={require(fadeImages[0])} alt="1"/>
            </div>
            <h2>First Slide</h2>
          </div>
          <div className="each-fade">
            <div className="image-container">
              <img src={require(fadeImages[1])} alt="2"/>
            </div>
            <h2>Second Slide</h2>
          </div>
          <div className="each-fade">
            <div className="image-container">
              <img src={require(fadeImages[2])} alt="3"/>
            </div>
            <h2>Third Slide</h2>
          </div>
        </Fade>
      </div>
    )
  }
export default Slideshow;
