import React, { useState, useEffect, useCallback } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import useInfiniteScroll from "@closeio/use-infinite-scroll";
import "./FetchContent.css";

const URL = "http://api-v2.filmnet.ir/widgets/home-page?";
export default function Example({ parentCallback }) {
  const ListLoading = ContentLoading(ContentItems);
  const [items, setItems] = useState([]);
  const [hasMore, setHasMore] = useState(false);
  const [page, loaderRef, scrollerRef] = useInfiniteScroll({ hasMore });
  const [next, setNext] = useState();

  useEffect(() => {
    getFetch();
  }, [page]);


  function getFetch() {
    let realPage = page+5;
    fetch(`${URL}offset=${realPage}&count=5`)
      .then((res) => res.json())
      .then((res) => {
        if (res.meta.next_url) {
          setItems((prev) => [...prev, res]);
          setNext(res.meta.next_url);
          setHasMore(re <=res.meta.total_items_count)
          console.log(page);
        } else {
          setHasMore(false);
        }
      });


  }

  // function fetchMore(total,count, nextItem) {
  //   if (total > 0) {
  //     total = total - count;
  //     if (total!==0) {
  //       getFetch(nextItem);
  //       return;
  //     }
  //   }
  //}
  return (
    <div ref={scrollerRef} className="scroll">
      {items.map((item, index) => (
        <ListLoading
          isLoading={false}
          repos={item}
          key={index}
          parentCallback={item}
        />
      ))}

      {hasMore && (
        <div ref={loaderRef} style={{ margin: "auto", textAlign: "center" }}>
          دریافت موارد بیشتر{" "}
        </div>
      )}

      {/* {console.log(items)} */}
    </div>
  );
}
