import React, { Fragment } from "react";
import StarIcon from "@material-ui/icons/Star";
import IMDB from "../../image/imdb.png";
import "./ContentItem.css";
const ContetntItem = (props) => {
  const val = Object.values(props);
  return (
    <Fragment>
      {val.map((repo) => {
        if (repo.type === "regular_carousel") {
          let item = repo.configuration.items;
          item.map((data) => {
            return (
              <div className="item" key={data.content_id}>
                <a href={data.video_content}>
                  <img src="..." className="item-img" alt="عکس فیلم" />
                  <div>
                    <p className="item-header">{`$...}`}</p>
                    <div className="item-text">
                      <span className="item-icon">
                        <StarIcon />{" "}
                      </span>
                      <span className="item-rate">{}</span>
                      <span className="item-imdb-text">{}</span>
                      <img src={IMDB} alt="IMDB" className="item-imdb-img" />
                    </div>
                  </div>
                </a>
              </div>
            );
          });
        }
      })}
    </Fragment>
  );
};
export default ContetntItem;
