import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import "./ContentImg.css";
// import Img from "../../image/filme1.jpg";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const ContentImg = (props) => {
  const classes = useStyles();
  const val = Object.values(props);
  return (
    <div className={classes.root}>
      {val.map((repo) => {
        if (repo.type === "poster") {
          return (
            <Grid container spacing={1}>
              <Grid item xs={12} key={repo.id}>
                <a href={repo.configuration.items.image_path} className="content-img">
                  <img src={repo.poster} alt="عکس فیلم" />
                </a>
              </Grid>
            </Grid>
          );
        }
      })}
    </div>
  );
};
export default ContentImg;
