import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slidesShow.css";
import { Button } from "@material-ui/core";

const settings = {
  dots: true,
  fade: true,
  infinite: true,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
  waitForAnimate: false,
};
const Slideshow = (props) => {
  const vals = Object.values(props);
  const val = vals[0].map((items) => {
    if (items.type === "main_slider") {
      const values = Object.values(items.configuration);
      const arr = values.map((value) => {
        return value;
      });
      return arr;
    }
  });
  const array = val.filter((item) => item !== undefined);
  const arr = array[0];
  //console.log(arr[0]);
  const  list = arr[0];
  //return <div>1</div>;
  const element= list.map((item) => {return Object.values(item)});
  console.log(element);
  return (
    <div>
      <Slider {...settings}>
         {element.map((item, index) => (
          <div val={console.log(item)} key={index}>
            <div
              val={console.log(item)}
              key={item.image_id}
              className="item-slider"
            >
              <img
                src={item.image_path}
                alt={item.title}
                key={index+item.image_id}
              />
            </div>
            <div className="slide-btns" key={item.image_id + index}>
              <Button href="/" className="slide-btn">
                خرید بلیط
              </Button>
            </div>*
          </div>
        ))}
        ; 
      </Slider>
    </div>
  );
};
export default Slideshow;
