import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";
export default function App() {
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });

  const [loadMore, setLoadMore] = useState({
    loading: false,
    repos: null,
    nextURL: "",
  });

  function LoadMore(url) {
    InfiniteLis("/widgets/home-page?count=5");
    useEffect(() => {
      getData(url, setLoadMore);
    }, [setLoadMore]);
  }
  function InfiniteLis(url) {
    useEffect(() => {
      getData(url, setState);
    }, [setState]);
  }
  function getData(url, set) {
    const apiurl = "http://api-v2.filmnet.ir";
    const URL = apiurl + url;
    if(state.nextURL) {
        url = apiurl+this.state.nextHref;
    }
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        set({
          loading: false,
          repos: res,
          nextURL: res.meta.next_url,
        });
      });
  }
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={this.loadItems.bind(this)}
      hasMore={this.state.hasMoreItems}
    >
      <ListLoading isLoading={state.loading} repos={state.repos} />
    </InfiniteScroll>
  );
}
