import React from "react";
import ContentHeader from "./ContentHeader";
import ContentImg from "./ContentImg";
import "./ContentItem.css";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));
const ContetntItems = (props) => {
  const { repos } = props;
  const classes = useStyles();
  if (!repos || repos.length === 0) return <p>server don't responce , sorry</p>;
  const val = Object.values(repos);
  return (
    <div className={classes.root}>
      {val.map((repo) => {
        return (
          <Grid container spacing={3} className="content-body">
            <Grid item xs={12} key={repo}>
              <ContentHeader {...repo} />
            </Grid>
            <Grid item xs={12} key={repo}>
              <ContentImg {...repo} />
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};

export default ContetntItems;
