import React from "react";
import ReactDOM from "react-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const photos = [
  {
    id: 1,
    title: "photo A",
    url: "https://static.filmnet.ir/images/94/51/94511e5d24fd4c799f94f16a83d1842f.jpg",
    topic: 'topic A'
  },
  {
    id: 2,
    title: "photo B",
    url: "https://static.filmnet.ir/images/74/24/7424f63029db41bd9b35df817983c55e.jpg",
    topic: 'topic B'
  },
  {
    id: 3,
    title: "photo C",
    url: "https://static.filmnet.ir/images/81/62/816279b8cbb54ba4b2911b7b59a2cd91.jpg",
    topic: 'topic C'
  }
];
class Slideshow extends Compo {
  render() {
    var settings = {
      dots: true,
      speed: 500,
    };
    return (
      <div style={{ padding: 24 }}>

        <Slider {...settings}>
          {photos.map(item => (
            <div key={item.id} className="item-slider" style={{
              background: 'url(' + require(`${item.url}`) + ')', backgroundRepeat: 'no-repeat'
            }}>
              <div className="caption">
                <h4>{item.topic}</h4>
                <p>{item.title}</p>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
  }
}

export default Slideshow;
