import React, { useEffect, useState } from "react";
import "./navBar.css";

const NavBarMenu = () => {
  const [state, setState] = useState([]);
  useEffect(() => {
    function getData() {
      var URL = "https://api-v2.filmnet.ir/hyperlinkMenus/";
      fetch(URL)
        .then((res) => res.json())
        .then((res) => {
          setState((prev) => [...prev, res]);
        });
    }
    getData();
  }, []);
  return (
    <ul className="navBarList">
      {state.map((item, index) => (
        <li className="navBarItem" key={index} val>
          <a href={item.display_title} className="navBarLink" key={item.id}>
           {item.display_title}
          </a>
        </li>
      ))}
    </ul>
  );
};
export default NavBarMenu;
