import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    hasMore:true,
    url:"/widgets/home-page?count=5",
  });

  useEffect(() => {
    setAppState({ loading: true });
    const apiUrl = `http://api-v2.filmnet.ir`;
    fetch(apiUrl+appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos,ulr:repos.meta.next_url });
        console.log(repos.meta);
      });
  }, [setAppState]);


  const fetchMoreData = () => {

    useEffect(() => {
    setAppState({ loading: true });

    fetch(apiUrl+appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos,ulr:repos.meta.next_url });
      });
  }, [setAppState]);
};
  return (
    <div id="scrollableDiv">
      <InfiniteScroll
        next={fetchMoreData}
        // hasMore={appState.hasMore}
        scrollableTarget="scrollableDiv"
      >
        {appState.repos.map((i, index) => (
          <div key={index}>
            <ListLoading isLoading={appState.loading} repos={appState.repos} />
          </div>
        ))}
      </InfiniteScroll>
      ;
    </div>
  );
};
export default FetchContent;
