import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });
  function FetchContent  (nextUrl) {
    // console.log(nextUrl);
    const apiUrl = "http://api-v2.filmnet.ir";
    useEffect(() => {
      if(appState.nextURL === null){
        const url ="/widgets/home-page?count=5"
        setAppState({ loading: true });
        fetch(apiUrl + url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({
              loading: false,
              repos: repos,
              nextURL: repos.meta.next_url,
            });
          });
      }
      else{
        setAppState({ loading: true });
        console.log(appState.nextURL);
        const url = apiUrl + appState.nextURL;
        console.log(url);
        fetch(url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({
              loading: false,
              repos: repos,
              nextURL: repos.meta.next_url,
            });
          }); 
        } 
    }, [setAppState]);
  };
  return (
    <InfiniteScroll
      dataLength={Object.values(appState)}
      next={FetchContent(appState.nextURL)}
      hasMore={true}
    >

      <ListLoading
        isLoading={appState.loading}
        repos={appState.repos}
      />

    </InfiniteScroll>
  );
};
export default FetchContents;
