import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slidesShow.css";
import { Button } from "@material-ui/core";


const settings = {
  dots: true,
  fade: true,
  infinite: true,
  speed: 500,
  slidesToShow: 1,
  slidesToScroll: 1,
};
const Slideshow = (props) => {
  const val = Object.values(props);

  return (
    <div>
      <Slider {...settings}>
        {val.map((items) => {
          if (items.type === "main_slider") {
            const value = Object.values(items.configuration);
            value.map((item) => (
              <div className="slides" key={item.image_id}>
                <div
                  key={item.image_id}
                  val={item.image_path}
                  className="item-slider"
                  style={{
                    backgroundRepeat: "no-repeat",
                    background:"url("+item.image_path+")",
                  }}
                ></div>
                <div className="slide-btns" key={item.image_id}>
                  <Button href="/" className="slide-btn">
                    خرید بلیط
                  </Button>
                </div>
              </div>
            ))};
        })};
      </Slider>
    </div>
  );
};

export default Slideshow;
