import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);
  const apiUrl = `http://api-v2.filmnet.ir`;
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    hasMore: false,
    url: "/widgets/home-page?count=5",

  });

  useEffect(() => {
    setAppState({ loading: true });

    fetch(apiUrl + appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos, url: repos.meta.next_url });
      });
      fetch(apiUrl + appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, data: repos, url: repos.meta.next_url });
      });
  }, [setAppState]);

  const fetchMore = () => {



  };
  return <ListLoading isLoading={appState.loading} repos={appState.repos} />;
};
export default FetchContent;
