import React, { useEffect, useState } from "react";
import ContentHeader from "./ContentHeader";
import ContentImg from "./ContentImg";
import "./ContentItem.css";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import InfiniteScroll from "react-infinite-scroll-component";
// import FetchContent from "./scrollFetch";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import FetchContent from "./FetchContent";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(0),
    textAlign: "start",
    color: theme.palette.text.primary,
    backgroundColor: theme.palette.background.default,
  },
}));
const ContetntItems = (props) => {

  const FetchContent = (url) => {
    // const ListLoading = ContentLoading(ContentItems);
    const apiUrl = `http://api-v2.filmnet.ir`;
    const [appState, setAppState] = useState({
      loading: false,
      repos: null,
    });
    useEffect(() => {
      setAppState({ loading: true });
      fetch(apiUrl + url)
        .then((res) => res.json())
        .then((repos) => {
          setAppState({ loading: false, repos: repos });
        });
    }, [setAppState]);
    // return <ListLoading isLoading={appState.loading} repos={appState.repos}/>;
  }
  const classes = useStyles();
  const { repos } = props;
  if (!repos || repos.length === 0) return <p>server don't responce , sorry</p>;
  const val = Object.values(repos);
  const baseURL = val[1].next_url;
  return (
    <div className={classes.root}>
      <InfiniteScroll
          dataLength={val.length}
          next={FetchContent()}
          hasMore={true}
        >
        {val.map((repo) => {
          return (
            <Grid container spacing={3} className="content-body">
              <Grid item xs={12} key={repo}>
                <Paper className={classes.paper} style={{ boxShadow: "none" }}>
                  <ContentHeader {...repo} />
                  <ContentImg {...repo} />
                </Paper>
              </Grid>
            </Grid>
          );
        })}
      </InfiniteScroll>
    </div>
  );
};

export default ContetntItems;
