import React, { useEffect, useState } from "react";
import ContentHeader from "./ContentHeader";
import ContentImg from "./ContentImg";
import "./ContentItem.css";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(0),
    textAlign: "start",
    color: theme.palette.text.primary,
    backgroundColor:theme.palette.background.default
  },
}));
const ContetntItems = (props) => {
  const [moreState, setMoreState] = useState({
    loading: false,
    repos: null,
    url:null
  });




  useEffect(() => {
    const { repos } = props;
  const classes = useStyles();
  if (!repos || repos.length === 0) return <p>server don't responce , sorry</p>;
  const val = Object.values(repos);
  const baseURL =val[1].next_url;
    const apiUrl = `http://api-v2.filmnet.ir`;
    setMoreState({ loading: true });
    const url="/widgets/home-page?count=5";
    fetch(baseURL +url)
      .then((res) => res.json())
      .then((repos) => {
        setMoreState({ loading: false, repos: repos,url: repos.meta.next_ur});
      });
  }, [setMoreState]);
  return (
    <div className={classes.root}>
      {val.map((repo) => {
        return (
          <Grid container spacing={3} className="content-body">
            <Grid item xs={12} key={repo} >
              <Paper className={classes.paper} style={{boxShadow:"none"}}>
                <ContentHeader {...repo} />
                <ContentImg {...repo} />
              </Paper>
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};

export default ContetntItems;
