import React from "react";
import Carousel from "react-material-ui-carousel";
import { Button, Paper, } from "@material-ui/core";

import "./../../image/7424f63029db41bd9b35df817983c55e.jpg";
import "./../../image/94511e5d24fd4c799f94f16a83d1842f.jpg";
import "./../../image/816279b8cbb54ba4b2911b7b59a2cd91.jpg";

const slideImages = [
  "https://static.filmnet.ir/images/94/51/94511e5d24fd4c799f94f16a83d1842f.jpg",
  "https://static.filmnet.ir/images/74/24/7424f63029db41bd9b35df817983c55e.jpg",
  "https://static.filmnet.ir/images/81/62/816279b8cbb54ba4b2911b7b59a2cd91.jpg",
];

function Example(props) {
  var items = [
    {
      name: "Random Name #1",
      description: "Probably the most random thing you have ever seen!",

    },
    {
      name: "Random Name #2",
      description: "Hello World!",
    },
  ];

  return (
    <Carousel>
      {items.map((item, i) => (
        <Item key={i} item={item} />
      ))}
    </Carousel>
  );
}
const Slideshow = (props) => {

  return (
    <Paper>
        <h2>{props.item.name}</h2>
        <p>{props.item.description}</p>

        <Button className="CheckButton">
            Check it out!
        </Button>
    </Paper>
)
};
export default Slideshow;
