    //   console.log(element);
    //   if (element === url[index]){
    //     console.log(url[index]);
    //     return url[index]
    //   }
    // }
    //setUrl(url);
    //
  }

  var i =0
  function getData(url) {
    i++;
    console.log(i);
    const apiurl = "http://api-v2.filmnet.ir";
    var URL = apiurl + url;
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        console.log(res);
        if (res.meta.next_url) {
          setState({
            loading: false,
            repos: res,
            nextURL: res.meta.next_url,
          });


        } else {
          setState({
            hasMoreItems: false,
          });
        }
        // setUrl((prev)=>[...prev,res.meta.next_url])
      });
  }
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={getData("/widgets/home-page?count=5")}
      hasMore={state.hasMoreItems}

      val={console.log(state)}
    >
      <ListLoading isLoading={state.loading} repos={state.repos} />
    </InfiniteScroll>
  );
}
