import React from 'react';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
export default function LabelBottomNavigation() {
  const [value, setValue] = React.useState("");

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <BottomNavigation value={value} onChange={handleChange} >
      <BottomNavigationAction className="filterBtn" label="فیلم" value="Film" />
      <BottomNavigationAction className="filterBtn" label="سریال" value="Serie" />
      <BottomNavigationAction className="filterBtn" label="مجموعه" value="Collection" />
    </BottomNavigation>
  );
}
