import React from "react";
import StarIcon from "@material-ui/icons/Star";
import IMDB from "../../image/imdb.png";
import "./ContentItem.css";

const ContetntItem = (props) => {
  const vals = Object.values(props);
  const element = Object.values(vals[1]);
  const items = element[0];
  const item = Object.values(items);
  return (
    <div className="items">
      {item.map((data) => {
        return (
          <div className="item" key={data.content_id}>
            <a key={data.video_content.short_id}
              href={
                "https://filmnet.ir/contents/" +
                data.video_content.short_id +
                "/" +
                data.video_content.supper_title
              }
            >
              <img
                key={data.video_content.cover_image.path}
                src={data.video_content.cover_image.path}
                className="item-img"
                alt="عکس فیلم"
              />
              <div>
                <p className="item-header" key={data.video_content.title}>{`${data.video_content.title}`}</p>
                <div className="item-text" key="icon-star">
                  <span className="item-icon" key="star">
                    <StarIcon />{" "}
                  </span>
                  <span className="item-rate" key={data.video_content.rate}>
                    {Math.round(data.video_content.rate) / 10}
                  </span>
                  <span className="item-imdb-text" key="imdb-">{}</span>
                  <img src={IMDB} key="IMDB" alt="IMDB" className="item-imdb-img" />
                </div>
              </div>
            </a>
          </div>
        );
      })}
    </div>
  );
};
export default ContetntItem;
