import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slidesShow.css";
import { Button } from "@material-ui/core";

const photos = [
  {
    id: 1,
    title: "photo A",
    background: "1.jpg",
    topic: "topic A",
  },
  {
    id: 2,
    title: "photo B",
    background: "2.jpg",
    topic: "topic B",
  },
  {
    id: 3,
    title: "photo C",
    background: "3.jpg",
    topic: "topic C",
  },
];
function Slideshow (props){
    const settings = {
      dots: true,
      fade: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };
    const vals = Object.values(props);
  const element = Object.values(vals[1]);
  const items = element[0];
  const item = Object.values(items);
    return (
      <div>
        <Slider {...settings}>
          {photos.map((item) => (
            <div className="slides" key={item.id + item.background}>
              <div
                key={item.id}
                className="item-slider"
                style={{
                  backgroundRepeat: "no-repeat",
                  background:
                    "url(" + require(`./image/${item.background}`) + ")",
                }}
              ></div>
              <div className="slide-btns">
                <Button href="/" className="slide-btn">خرید بلیط</Button>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
}

export default Slideshow;
